app.controller("activeListCtrl",function(e,t){e.id=t.id,e.menuHandler="menu-bar",e.hamburgerHandler="hamburger",e.currentTask=0,e.selectedList=[{title:"Title",checked:!1},{title:"Title2",checked:!1},{title:"Title3",checked:!1},{title:"Title4",checked:!1},{title:"Title5",checked:!0},{title:"Title6",checked:!1}],e.completeTask=function(t){e.selectedList[t].checked=e.selectedList[t].checked?!1:!0,console.log(t),e.selectedList[t].checked?e.currentTask--:e.currentTask++},e.init=function(){for(var t=0;t<e.selectedList.length;t++)0==e.selectedList[t].checked&&e.currentTask++},e.deleteItem=function(t){confirm("Are you sure you want to delete "+e.selectedList[t].title+"?")&&(e.selectedList.splice(t,1),e.currentTask--)},e.addItem=function(){e.selectedList.push({id:e.selectedList.length+1,title:e.itemTitle,complete:!1}),e.itemTitle="",e.currentTask++},e.menuAction=function(){"menu-bar"==e.menuHandler?(e.menuHandler="menu-bar-open",e.hamburgerHandler="hamburger-open"):(e.menuHandler="menu-bar",e.hamburgerHandler="hamburger")},e.search=function(){$(".search-bar").slideToggle("slow"),e.menuAction()},e.openModal=function(){$("#overlay").fadeIn().find(".modalen").fadeIn(),e.menuAction()},e.closeModal=function(){$("#overlay").fadeOut().find(".modalen").fadeOut()}});