app.controller("listCtrl",["$scope",function(t){t.db=new PouchDB("dotlist"),t.allLists=[],t.list=[],t.listTitle="",t.menuHandler="menu-bar",t.hamburgerHandler="hamburger",t.listOptions=function(t){console.log($(t.target).parent().parent().find(".list-options")),$(t.target).parent().parent().find(".list-options").slideToggle().css("display","flex")},t.deleteList=function(e){confirm("Are you sure you want to delete "+t.allLists[e].title+"?")&&(t.allLists.splice(e,1),t.saveLists())},t.init=function(){t.db.info().then(function(e){console.log(e),0==e.doc_count&&t.db.put({_id:"lists",lists:[]})}),t.getLists()},t.addItem=function(){t.list.push({id:t.list.length+1,title:t.itemTitle,complete:!1}),t.itemTitle=""},t.deleteItem=function(e){confirm("Are you sure you want to delete "+t.list[e].title+"?")&&t.list.splice(e,1)},t.checkItem=function(e){t.list[e].complete=t.list[e].complete?!1:!0},t.createList=function(){t.allLists.push({title:t.listTitle,items:t.list,id:t.allLists.length+1}),console.log(t.allLists),t.list=[],t.listTitle="",$("#overlay").hide(),$(".modalen").hide(),t.saveLists()},t.menuAction=function(){"menu-bar"==t.menuHandler?(t.menuHandler="menu-bar-open",t.hamburgerHandler="hamburger-open"):(t.menuHandler="menu-bar",t.hamburgerHandler="hamburger")},t.search=function(){$(".search-bar").slideToggle("slow")},t.openModal=function(){$("#overlay").fadeIn().find(".modalen").fadeIn()},t.closeModal=function(){$("#overlay").fadeOut().find(".modalen").fadeOut()},t.getLists=function(){t.db.get("lists").then(function(e){console.log(e.lists),t.$apply(function(){t.allLists=e.lists})})},t.saveLists=function(){t.db.get("lists").then(function(e){return t.db.put({_id:e._id,_rev:e._rev,lists:t.allLists})})},t.flipped=!1,t.flip=function(){t.flipped?($("#first-side").css({transform:"rotateX(0deg)"}),t.flipped=!1):($("#first-side").css({transform:"rotateX(180deg)"}),t.flipped=!0)}}]);