app.controller("listCtrl",["$scope",function(e){e.db=new PouchDB("dotlist"),e.allLists=[],e.list=[],e.listTitle="",e.menuHandler="menu-bar",e.hamburgerHandler="hamburger",e.init=function(){e.db.info().then(function(t){console.log(t),0==t.doc_count&&e.db.put({_id:"lists",lists:[]})}),e.getLists()},e.addItem=function(){e.list.push({id:e.list.length+1,title:e.itemTitle,complete:!1}),e.itemTitle=""},e.deleteItem=function(t){confirm("Are you sure you want to delete "+e.list[t].title+"?")&&e.list.splice(t,1)},e.checkItem=function(t){e.list[t].complete=e.list[t].complete?!1:!0},e.createList=function(){e.allLists.push({title:e.listTitle,items:e.list,id:e.allLists.length+1}),console.log(e.allLists),e.list=[],e.listTitle="",$("#overlay").hide(),$(".modalen").hide(),e.saveLists()},e.menuAction=function(){"menu-bar"==e.menuHandler?(e.menuHandler="menu-bar-open",e.hamburgerHandler="hamburger-open",console.log("SuckaDick")):(e.menuHandler="menu-bar",e.hamburgerHandler="hamburger",console.log("SuckNoDick"))},e.search=function(){$(".search-bar").slideToggle("slow"),e.menuAction()},e.openModal=function(){$("#overlay").fadeIn().find(".modalen").fadeIn(),e.menuAction()},e.closeModal=function(){$("#overlay").fadeOut().find(".modalen").fadeOut()},e.getLists=function(){e.db.get("lists").then(function(t){console.log(t.lists),e.$apply(function(){e.allLists=t.lists})})},e.saveLists=function(){e.db.get("lists").then(function(t){return e.db.put({_id:t._id,_rev:t._rev,lists:e.allLists})})}}]);