app.controller("mainCtrl",["$scope","ajaxFactory","$state",function(e,o,r){e.login=function(){o.ajax({data:{email:e.username,password:e.password},method:"POST",url:"/AjaxLogin"},function(o){if(""===o)e.errormsg="Wrong email or password! try again";else{var o=JSON.parse(o);$.cookie("userId",o[0].id),console.log(o),r.transitionTo("lists")}},function(){})},e.regUser=function(){e.regPassword===e.regRetype?o.ajax({data:{email:e.regEmail,username:e.regUsername,password:e.regPassword,confirmPassword:e.regRetype},method:"POST",url:"/AjaxRegister"},function(e){console.log(e)},function(){}):e.errormsg="Your password doesnt match"},e.flipped=!1,e.flip=function(){e.flipped?($("#login-side").css({transform:"rotateY(0deg)"}),e.flipped=!1):($("#login-side").css({transform:"rotateY(180deg)"}),e.flipped=!0)}}]);